import React from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Button, Chip, Breadcrumbs, Link } from '@mui/material';
import productos from '../../data/productos.json';
import { useAccessibility } from '../../context/AccessibilityContext';

export default function ProductoDetalle() {
  const { id } = useParams();
  const { modoContraste } = useAccessibility();

  const producto = productos.find(p => String(p.id) === id);

  if (!producto) {
    return (
      <Box sx={{ mt: 8, px: 4, py: 6 }}>
        <Typography variant="h6" color="error">Producto no encontrado</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{
      mt: 8,
      px: 4,
      py: 6,
      maxWidth: '1000px',
      mx: 'auto',
      backgroundColor: modoContraste ? '#212121' : '#f9fbe7',
      color: modoContraste ? '#fff' : 'inherit',
      borderRadius: 4,
      boxShadow: '0 6px 20px rgba(0,0,0,0.1)'
    }}>
      
      {/* Breadcrumb */}
      <Breadcrumbs separator="›" sx={{ mb: 2 }}>
        <Link href="/" color={modoContraste ? '#ffd900' : '#2e7d32'}>Inicio</Link>
        <Link href={`/productos/${producto.categoria.toLowerCase()}`} color={modoContraste ? '#ffd900' : '#2e7d32'}>
          {producto.categoria}
        </Link>
        <Typography>{producto.nombre}</Typography>
      </Breadcrumbs>

      {/* Imagen del producto */}
      <Box sx={{ mb: 4 }}>
        <img
          src={producto.imagen}
          alt={producto.nombre}
          style={{ width: '100%', borderRadius: 12, boxShadow: '0 4px 14px rgba(0,0,0,0.1)' }}
        />
      </Box>

      {/* Título y categoría */}
      <Typography variant="h4" sx={{ fontWeight: 'bold', mb: 1 }}>
        {producto.nombre}
      </Typography>
      <Typography variant="subtitle1" sx={{ mb: 2 }}>
        {producto.categoria}
      </Typography>
      {producto.destacado && (
        <Chip label={producto.destacado} color="success" sx={{ mb: 2 }} />
      )}

      {/* Descripción */}
      <Typography variant="body1" sx={{ mb: 4 }}>
        {producto.descripcion}
      </Typography>

      {/* Botones institucionales */}
      <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
        {producto.botones?.map((btn, index) => (
          <Button
            key={index}
            variant="contained"
            href={btn.url}
            target="_blank"
            sx={{
              bgcolor: modoContraste ? '#ffd900' : '#2e7d32',
              color: modoContraste ? '#000' : '#fff',
              fontWeight: 'bold',
              '&:hover': { bgcolor: modoContraste ? '#ffeb3b' : '#1b5e20' }
            }}
          >
            {btn.label}
          </Button>
        ))}
      </Box>
    </Box>
  );
}
