import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  CircularProgress,
  Alert,
} from '@mui/material';

export default function AdminEditarProducto() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [producto, setProducto] = useState({
    nombre: '',
    categoria: '',
    precio: '',
    descripcion: '',
    imagen: '',
    descuento: '',
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [guardando, setGuardando] = useState(false);

  // Cargar datos producto
  useEffect(() => {
    async function fetchProducto() {
      try {
        const res = await fetch(`http://localhost:5000/api/productos/${id}`);
        if (!res.ok) throw new Error('Error al cargar el producto');
        const data = await res.json();
        setProducto({
          nombre: data.nombre || '',
          categoria: data.categoria || '',
          precio: data.precio?.toString() || '',
          descripcion: data.descripcion || '',
          imagen: data.imagen || '',
          descuento: data.descuento?.toString() || '',
        });
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    }
    fetchProducto();
  }, [id]);

  // Actualizar inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProducto((prev) => ({ ...prev, [name]: value }));
  };

  // Guardar cambios
  const handleSubmit = async (e) => {
    e.preventDefault();
    setGuardando(true);
    setError(null);

    // Validar campos mínimos
    if (!producto.nombre || !producto.categoria || !producto.precio) {
      setError('Nombre, categoría y precio son obligatorios.');
      setGuardando(false);
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/api/productos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...producto,
          precio: parseFloat(producto.precio),
          descuento: producto.descuento ? parseInt(producto.descuento) : 0,
        }),
      });
      if (!res.ok) throw new Error('Error al guardar producto');
      navigate('/productos');
    } catch (err) {
      setError(err.message);
    } finally {
      setGuardando(false);
    }
  };

  if (loading) return (
    <Box
      sx={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}
    >
      <CircularProgress color="success" />
    </Box>
  );

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: '#e8f5e9',
        p: { xs: 3, md: 6 },
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
      }}
    >
      <Paper
        elevation={8}
        sx={{
          width: { xs: '100%', md: 700 },
          p: 5,
          borderRadius: 4,
          bgcolor: 'white',
          boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)',
        }}
      >
        <Typography
          variant="h4"
          color="success.main"
          sx={{ mb: 3, fontWeight: 'bold', textAlign: 'center' }}
        >
          Editar Producto
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <form onSubmit={handleSubmit} noValidate>
          <TextField
            fullWidth
            label="Nombre"
            name="nombre"
            value={producto.nombre}
            onChange={handleChange}
            margin="normal"
            required
            autoFocus
          />

          <TextField
            fullWidth
            label="Categoría"
            name="categoria"
            value={producto.categoria}
            onChange={handleChange}
            margin="normal"
            required
            helperText="Ejemplo: Mascotas, Químicos, Jardinería"
          />

          <TextField
            fullWidth
            label="Precio (USD)"
            name="precio"
            type="number"
            value={producto.precio}
            onChange={handleChange}
            margin="normal"
            required
            inputProps={{ min: 0, step: "0.01" }}
          />

          <TextField
            fullWidth
            label="Descuento (%)"
            name="descuento"
            type="number"
            value={producto.descuento}
            onChange={handleChange}
            margin="normal"
            inputProps={{ min: 0, max: 100 }}
            helperText="Opcional"
          />

          <TextField
            fullWidth
            label="Imagen (URL)"
            name="imagen"
            value={producto.imagen}
            onChange={handleChange}
            margin="normal"
            placeholder="https://ejemplo.com/imagen.jpg"
          />

          <TextField
            fullWidth
            label="Descripción"
            name="descripcion"
            value={producto.descripcion}
            onChange={handleChange}
            margin="normal"
            multiline
            rows={4}
          />

          <Box
            sx={{ mt: 4, display: 'flex', justifyContent: 'space-between', gap: 2 }}
          >
            <Button
              variant="outlined"
              color="error"
              onClick={() => navigate('/productos')}
              sx={{ flex: 1 }}
            >
              Cancelar
            </Button>

            <Button
              type="submit"
              variant="contained"
              color="success"
              disabled={guardando}
              sx={{ flex: 1 }}
            >
              {guardando ? 'Guardando...' : 'Guardar Cambios'}
            </Button>
          </Box>
        </form>
      </Paper>
    </Box>
  );
}
