'use client';

import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  MenuItem,
  InputLabel,
  FormControl,
  Select,
  OutlinedInput,
  Grid
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import CancelIcon from '@mui/icons-material/Cancel';
import { styled } from '@mui/system';

const tiposServicio = [
  'CotizaciÃ³n agrÃ­cola',
  'Consulta veterinaria',
  'Laboratorio tÃ©cnico',
  'Soporte rural',
  'Rastreo de pedido'
];

// Estilos personalizados
const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(5),
  borderRadius: 20,
  maxWidth: 700,
  margin: 'auto',
  background: 'linear-gradient(145deg, #f1f8e9, #e8f5e9)',
  boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
}));

const StyledButton = styled(Button)(({ theme }) => ({
  backgroundColor: '#2e7d32',
  color: '#fff',
  fontWeight: 600,
  fontSize: '16px',
  textTransform: 'none',
  borderRadius: 12,
  padding: '10px 24px',
  '&:hover': {
    backgroundColor: '#1b5e20',
  },
}));

const CancelButton = styled(Button)(({ theme }) => ({
  backgroundColor: '#d32f2f',
  color: '#fff',
  fontWeight: 600,
  fontSize: '16px',
  textTransform: 'none',
  borderRadius: 12,
  padding: '10px 24px',
  '&:hover': {
    backgroundColor: '#b71c1c',
  },
}));

export default function FormularioServicio() {
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    tipo: '',
    mensaje: ''
  });

  const [errors, setErrors] = useState({});

  const validate = () => {
    let temp = {};
    temp.nombre = formData.nombre ? '' : 'El nombre es obligatorio';
    temp.email = /\S+@\S+\.\S+/.test(formData.email) ? '' : 'Correo invÃ¡lido';
    temp.tipo = formData.tipo ? '' : 'Selecciona un tipo de servicio';
    temp.mensaje = formData.mensaje.length >= 10 ? '' : 'MÃ­nimo 10 caracteres';
    setErrors(temp);
    return Object.values(temp).every(x => x === '');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      console.log('Solicitud enviada:', formData);
      alert('âœ… Solicitud enviada correctamente (modo local)');
      setFormData({ nombre: '', email: '', tipo: '', mensaje: '' });
    }
  };

  const handleCancel = () => {
    if (confirm('Â¿EstÃ¡s seguro de cancelar la solicitud?')) {
      setFormData({ nombre: '', email: '', tipo: '', mensaje: '' });
    }
  };

  return (
    <Box sx={{ pt: 10, px: { xs: 2, md: 4 }, pb: 6, background: '#f5f5f5', minHeight: '100vh' }}>
      <Typography
        variant="h4"
        sx={{
          fontWeight: 'bold',
          textAlign: 'center',
          color: '#2e7d32',
          mb: 5,
        }}
      >
        ðŸŒ± Solicita tu Servicio en LÃ­nea
      </Typography>

      <StyledPaper elevation={3}>
        <form onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                label="Nombre completo"
                name="nombre"
                fullWidth
                required
                value={formData.nombre}
                onChange={handleChange}
                error={Boolean(errors.nombre)}
                helperText={errors.nombre}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Correo electrÃ³nico"
                name="email"
                type="email"
                fullWidth
                required
                value={formData.email}
                onChange={handleChange}
                error={Boolean(errors.email)}
                helperText={errors.email}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required error={Boolean(errors.tipo)}>
                <InputLabel id="tipo-label">Tipo de servicio</InputLabel>
                <Select
                  labelId="tipo-label"
                  name="tipo"
                  value={formData.tipo}
                  onChange={handleChange}
                  input={<OutlinedInput label="Tipo de servicio" />}
                >
                  {tiposServicio.map((tipo, idx) => (
                    <MenuItem key={idx} value={tipo}>
                      {tipo}
                    </MenuItem>
                  ))}
                </Select>
                <Typography variant="caption" color="error">
                  {errors.tipo}
                </Typography>
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Mensaje o detalle del servicio"
                name="mensaje"
                multiline
                rows={4}
                fullWidth
                required
                value={formData.mensaje}
                onChange={handleChange}
                error={Boolean(errors.mensaje)}
                helperText={errors.mensaje}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <StyledButton
                type="submit"
                fullWidth
                endIcon={<SendIcon />}
              >
                Enviar Solicitud
              </StyledButton>
            </Grid>
            <Grid item xs={12} sm={6}>
              <CancelButton
                type="button"
                fullWidth
                onClick={handleCancel}
                endIcon={<CancelIcon />}
              >
                Cancelar Solicitud
              </CancelButton>
            </Grid>
          </Grid>
        </form>
      </StyledPaper>
    </Box>
  );
}
