import React from 'react';
import { Modal, Box, IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import LoginForm from '../forms/LoginForm';

export default function LoginModal({ onClose }) {
  return (
    <Modal open onClose={onClose} aria-labelledby="modal-login" aria-describedby="login-accessible">
      <Box
        role="dialog"
        aria-modal="true"
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          bgcolor: 'white',
          p: 4,
          borderRadius: 3,
          width: { xs: '90%', sm: 400 },
          boxShadow: 24,
          outline: 0
        }}
      >
        {/* Bot√≥n de cerrar */}
        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 1 }}>
          <IconButton onClick={onClose} aria-label="Cerrar login">
            <CloseIcon />
          </IconButton>
        </Box>

        {/* LoginForm heredado */}
        <LoginForm onClose={onClose} />
      </Box>
    </Modal>
  );
}
