import React, { useState, useContext } from 'react';
import {
  Box, Fab, Popper, Paper, Button, Typography, Fade
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto
  } = useContext(AccessibilityContext);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
    setOpen((prev) => !prev);
  };

  return (
    <Box sx={{ position: 'fixed', bottom: 24, right: 24 }}>
      <Fab color="primary" onClick={handleClick} aria-label="Accesibilidad">
        <AccessibilityNewIcon />
      </Fab>

      <Popper open={open} anchorEl={anchorEl} transition placement="top-end">
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={250}>
            <Paper elevation={4} sx={{ p: 2, mt: 1, minWidth: 220 }}>
              <Typography variant="h6" gutterBottom>
                ⚙️ Accesibilidad
              </Typography>

              <Button
                variant="contained"
                color={altoContraste ? 'secondary' : 'primary'}
                fullWidth
                onClick={toggleContraste}
                sx={{ mb: 1 }}
              >
                {altoContraste ? 'Desactivar Contraste' : 'Activar Contraste'}
              </Button>

              <Button
                variant="contained"
                color={modoDislexia ? 'secondary' : 'primary'}
                fullWidth
                onClick={toggleDislexia}
                sx={{ mb: 1 }}
              >
                {modoDislexia ? 'Desactivar Dislexia' : 'Activar Dislexia'}
              </Button>

              <Typography variant="caption" color="text.secondary" sx={{ mt: 1 }}>
                    Escala de texto: <strong>{Math.round(tamañoTexto * 100)}%</strong>
                </Typography>
              <Box sx={{ mt: 1, display: 'flex', justifyContent: 'space-between' }}>
                <Button onClick={disminuirTexto} variant="outlined" size="small">–</Button>
                <Button onClick={aumentarTexto} variant="outlined" size="small">+</Button>
              </Box>
            </Paper>
          </Fade>
        )}
      </Popper>
    </Box>
  );
}
