import React, { useState, useContext, useEffect } from 'react';
import {
  Box,
  Fab,
  Dialog,
  DialogContent,
  DialogTitle,
  Button,
  Typography,
  IconButton
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import CloseIcon from '@mui/icons-material/Close';
import ContrastIcon from '@mui/icons-material/Tonality';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import PsychologyIcon from '@mui/icons-material/Psychology';
import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [sobreBanner, setSobreBanner] = useState(true);

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto
  } = useContext(AccessibilityContext);

  useEffect(() => {
    const handleScroll = () => {
      setSobreBanner(window.scrollY < 600);
    };
    window.addEventListener('scroll', handleScroll);
    handleScroll();
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const estiloBoton = sobreBanner
    ? {
        background: 'linear-gradient(135deg, #1976d2, #7e57c2)',
        color: '#fff',
        boxShadow: '0 8px 24px rgba(0,0,0,0.4)',
        width: 64,
        height: 64,
        borderRadius: '50%',
        '&:hover': {
          transform: 'scale(1.1)',
          boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
        },
        transition: 'all 0.3s ease-in-out'
      }
    : {
        backgroundColor: '#ffffffee',
        color: '#1976d2',
        border: '2px solid #1976d2',
        boxShadow: '0 8px 24px rgba(0,0,0,0.35)',
        width: 64,
        height: 64,
        borderRadius: '16px',
        backdropFilter: 'blur(6px)',
        '&:hover': {
          backgroundColor: '#ffffff',
          transform: 'scale(1.1)',
          boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
        },
        transition: 'all 0.3s ease-in-out'
      };

  return (
    <>
      <Box sx={{ position: 'fixed', bottom: 24, right: 24, zIndex: 1300 }}>
        <Fab onClick={() => setOpen(true)} aria-label="Accesibilidad" sx={estiloBoton}>
          <AccessibilityNewIcon fontSize="large" />
        </Fab>
      </Box>

      <Dialog
        open={open}
        onClose={() => setOpen(false)}
        maxWidth="xs"
        fullWidth
        PaperProps={{
          sx: {
            borderRadius: '20px',
            p: 3,
            backgroundColor: '#fdfdfd',
            boxShadow: '0 12px 36px rgba(0,0,0,0.2)'
          }
        }}
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
          <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2' }}>
            ⚙️ Opciones de accesibilidad
          </Typography>
          <IconButton onClick={() => setOpen(false)} edge="end">
            <CloseIcon />
          </IconButton>
        </DialogTitle>

        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Button
              variant="contained"
              startIcon={<ContrastIcon />}
              color={altoContraste ? 'secondary' : 'primary'}
              fullWidth
              onClick={toggleContraste}
              sx={{ textTransform: 'none', fontWeight: 500 }}
            >
              {altoContraste ? 'Desactivar contraste alto' : 'Activar contraste alto'}
            </Button>

            <Button
              variant="contained"
              startIcon={<PsychologyIcon />}
              color={modoDislexia ? 'secondary' : 'primary'}
              fullWidth
              onClick={toggleDislexia}
              sx={{ textTransform: 'none', fontWeight: 500 }}
            >
              {modoDislexia ? 'Desactivar modo dislexia' : 'Activar modo dislexia'}
            </Button>

            <Box>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                Escala de texto: <strong>{Math.round(tamañoTexto * 100)}%</strong>
              </Typography>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Button
                  onClick={disminuirTexto}
                  variant="outlined"
                  size="small"
                  startIcon={<TextFieldsIcon sx={{ fontSize: 16 }} />}
                  sx={{ minWidth: 40 }}
                >
                  –
                </Button>
                <Button
                  onClick={aumentarTexto}
                  variant="outlined"
                  size="small"
                  startIcon={<TextFieldsIcon sx={{ fontSize: 16 }} />}
                  sx={{ minWidth: 40 }}
                >
                  +
                </Button>
              </Box>
            </Box>
          </Box>
        </DialogContent>
      </Dialog>
    </>
  );
}
