import React from 'react';
import { Box, Typography } from '@mui/material';

export default function ProductoCard({ producto }) {
  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      {/* Imagen del producto */}
      <Box
        sx={{
          mb: 2,
          borderRadius: 2,
          overflow: 'hidden',
          height: 180,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
          transition: 'transform 0.3s ease',
          '& img': {
            transition: 'transform 0.3s ease'
          },
          '&:hover img': {
            transform: 'scale(1.05)'
          }
        }}
      >
        <img
          src={producto.imagen}
          alt={producto.nombre}
          style={{
            maxWidth: '100%',
            width: '100%',
            maxHeight: '100%',
            objectFit: 'contain',
            display: 'block'
          }}
        />
      </Box>

      {/* Nombre del producto */}
      <Typography
        variant="h6"
        sx={{ fontWeight: 'bold', mb: 1 }}
      >
        {producto.nombre}
      </Typography>

      {/* Descripci√≥n breve */}
      <Typography
        variant="body2"
        sx={{
          color: 'text.secondary',
          flexGrow: 1
        }}
      >
        {producto.descripcion}
      </Typography>
    </Box>
  );
}
