import React from 'react';
import { Box, Typography } from '@mui/material';

export default function ProductoCard({ producto }) {
  return (
    <Box
      sx={{
        width: 250,
        height: 340, 
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        borderRadius: 2,
        boxShadow: 3,
        overflow: 'hidden', 
        p: 2,
        backgroundColor: '#fff'
      }}
    >
      {/* Imagen */}
      <Box
        sx={{
          height: 180,
          borderRadius: 2,
          overflow: 'hidden',
          mb: 2,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
          transition: 'transform 0.3s ease',
          '& img': {
            transition: 'transform 0.3s ease'
          },
          '&:hover img': {
            transform: 'scale(1.05)'
          }
        }}
      >
        <img
          src={producto.imagen}
          alt={producto.nombre}
          style={{
            maxWidth: '100%',
            width: '100%',
            maxHeight: '100%',
            objectFit: 'contain',
            display: 'block'
          }}
        />
      </Box>

      {/* Nombre */}
      <Typography
        variant="h6"
        noWrap 
        sx={{ fontWeight: 'bold', mb: 1 }}
      >
        {producto.nombre}
      </Typography>

      {/* Descripci√≥n truncada */}
      <Typography
        variant="body2"
        sx={{
          color: 'text.secondary',
          overflow: 'hidden',
          textOverflow: 'ellipsis',
          display: '-webkit-box',
          WebkitLineClamp: 2,
          WebkitBoxOrient: 'vertical',
          flexGrow: 1
        }}
      >
        {producto.descripcion}
      </Typography>
    </Box>
  );
}
