import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Grid,
  Button,
  Snackbar,
  Alert,
  MenuItem,
  FormControlLabel,
  Switch,
  InputAdornment
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import CategoryIcon from '@mui/icons-material/Category';
import InventoryIcon from '@mui/icons-material/Inventory';
import TagIcon from '@mui/icons-material/LocalOffer';
import CheckIcon from '@mui/icons-material/CheckCircle';

const categorias = [
  'Alimento Perro',
  'Alimento Gato',
  'Limpieza y Aseo',
  'Jardinería',
  'Banano',
  'Acultura'
];

export default function EditarProductoForm() {
  const { id } = useParams(); // id es _id de Mongo
  const navigate = useNavigate();

  const [producto, setProducto] = useState(null);
  const [errores, setErrores] = useState({});
  const [confirmacion, setConfirmacion] = useState('');
  const [sinCambios, setSinCambios] = useState(false);
  const [productoOriginal, setProductoOriginal] = useState(null);

  // Traer producto desde la API
  useEffect(() => {
    fetch(`http://localhost:5000/api/productos/${id}`)
      .then(res => res.json())
      .then(data => {
        if (!data) return navigate('/');
        setProducto(data);
        setProductoOriginal(data);
      })
      .catch(() => navigate('/'));
  }, [id, navigate]);

  const validar = () => {
    const nuevosErrores = {};
    if (!producto.nombre?.trim()) nuevosErrores.nombre = 'Nombre obligatorio';
    if (!producto.precio || isNaN(producto.precio)) nuevosErrores.precio = 'Precio válido requerido';
    if (!producto.categoria) nuevosErrores.categoria = 'Selecciona una categoría';
    if (!producto.stock || isNaN(producto.stock)) nuevosErrores.stock = 'Stock válido requerido';
    setErrores(nuevosErrores);
    return Object.keys(nuevosErrores).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validar()) return;

    const hayCambios = JSON.stringify(producto) !== JSON.stringify(productoOriginal);
    if (!hayCambios) {
      setSinCambios(true);
      return;
    }

    try {
      await fetch(`http://localhost:5000/api/productos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(producto)
      });
      setConfirmacion('¡Producto actualizado correctamente!');
      setTimeout(() => navigate(-1), 2000);
    } catch (error) {
      console.error('Error al actualizar:', error);
    }
  };

  const handleCancel = () => navigate(-1);

  if (!producto) return null;

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ maxWidth: 720, mx: 'auto', mt: 6, p: 4, boxShadow: 4, bgcolor: '#fafafa', borderRadius: 4, display: 'flex', flexDirection: 'column', gap: 4 }}>
      <Typography variant="h4" sx={{ fontWeight: 'bold', textAlign: 'center', color: '#2e7d32' }}>
        Editar producto
      </Typography>

      <TextField label="ID (Mongo)" value={producto._id} fullWidth disabled sx={{ opacity: 0.7 }} />

      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Nombre del producto"
            fullWidth
            value={producto.nombre}
            onChange={(e) => setProducto({ ...producto, nombre: e.target.value })}
            error={!!errores.nombre}
            helperText={errores.nombre}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <TagIcon sx={{ color: '#777' }} />
                </InputAdornment>
              )
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            select
            label="Categoría"
            fullWidth
            value={producto.categoria}
            onChange={(e) => setProducto({ ...producto, categoria: e.target.value })}
            error={!!errores.categoria}
            helperText={errores.categoria}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <CategoryIcon sx={{ color: '#777' }} />
                </InputAdornment>
              )
            }}
          >
            {categorias.map(cat => (
              <MenuItem key={cat} value={cat}>{cat}</MenuItem>
            ))}
          </TextField>
        </Grid>
      </Grid>

      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Precio"
            type="number"
            fullWidth
            value={producto.precio || ''}
            onChange={(e) => setProducto({ ...producto, precio: e.target.value })}
            error={!!errores.precio}
            helperText={errores.precio}
            InputProps={{ startAdornment: <InputAdornment position="start">$</InputAdornment> }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Stock"
            type="number"
            fullWidth
            value={producto.stock || ''}
            onChange={(e) => setProducto({ ...producto, stock: e.target.value })}
            error={!!errores.stock}
            helperText={errores.stock}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <InventoryIcon sx={{ color: '#777' }} />
                </InputAdornment>
              )
            }}
          />
        </Grid>
      </Grid>

      <TextField
        label="Descripción"
        multiline
        rows={4}
        fullWidth
        value={producto.descripcion || ''}
        onChange={(e) => setProducto({ ...producto, descripcion: e.target.value })}
      />

      <TextField
        label="Descuento (%)"
        type="number"
        fullWidth
        value={producto.descuento || ''}
        onChange={(e) => setProducto({ ...producto, descuento: e.target.value })}
      />

      <FormControlLabel
        control={
          <Switch
            checked={producto.disponible || false}
            onChange={(e) => setProducto({ ...producto, disponible: e.target.checked })}
            color="success"
          />
        }
        label="Disponible para compra"
      />

      <Box sx={{ display: 'flex', gap: 2 }}>
        <Button type="submit" variant="contained" fullWidth sx={{ bgcolor: '#388e3c', fontWeight: 'bold' }}>
          Actualizar producto
        </Button>
        <Button variant="outlined" color="secondary" fullWidth onClick={handleCancel}>
          Cancelar
        </Button>
      </Box>

      <Snackbar open={!!confirmacion} autoHideDuration={4000} onClose={() => setConfirmacion('')} anchorOrigin={{ vertical: 'top', horizontal: 'center' }}>
        <Alert severity="success" variant="filled" icon={<CheckIcon fontSize="inherit" />} onClose={() => setConfirmacion('')} sx={{ bgcolor: '#1976d2', color: 'white' }}>
          {confirmacion}
        </Alert>
      </Snackbar>

      <Snackbar open={sinCambios} autoHideDuration={3000} onClose={() => setSinCambios(false)} anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}>
        <Alert severity="info" variant="standard" onClose={() => setSinCambios(false)}>
          No se detectaron cambios en el formulario.
        </Alert>
      </Snackbar>
    </Box>
  );
}
