import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Box, Typography, Button, Breadcrumbs, Link, Stack
} from '@mui/material';
import { useAccessibility } from '../../context/AccessibilityContext';
import productos from '../../data/productos.json';

export default function ProductoDetalle() {
  const { id } = useParams();
  const { altoContraste } = useAccessibility();

  const producto = productos.find(p => String(p.id) === id);

  if (!producto) {
    return (
      <Box sx={{ mt: 8, px: 4 }}>
        <Typography variant="h6" color="error">Producto no encontrado</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{
      mt: 6,
      px: 4,
      maxWidth: '900px',
      mx: 'auto',
      color: altoContraste ? '#fff' : '#000'
    }}>

      {/* Navegación tipo breadcrumb */}
      <Breadcrumbs separator="›" sx={{ mb: 3 }}>
        <Link href="/" color={altoContraste ? '#ffd900' : '#2e7d32'}>Inicio</Link>
        <Link href={`/productos/${producto.categoria.toLowerCase()}`} color={altoContraste ? '#ffd900' : '#2e7d32'}>
          {producto.categoria}
        </Link>
        <Typography>{producto.nombre}</Typography>
      </Breadcrumbs>

      {/* Título institucional */}
      <Typography variant="h2" sx={{ fontWeight: 'bold', mb: 1 }}>
        {producto.nombre}
      </Typography>
      <Typography variant="subtitle1" sx={{ mb: 4, color: altoContraste ? '#ffd900' : '#2e7d32' }}>
        {producto.categoria}
      </Typography>

      {/* Imagen */}
      <Box sx={{ mb: 4 }}>
        <img
          src={producto.imagen}
          alt={producto.nombre}
          style={{
            width: '100%',
            borderRadius: 16,
            boxShadow: altoContraste
              ? '0 0 0 2px #ffd900'
              : '0 4px 12px rgba(0,0,0,0.1)'
          }}
        />
      </Box>

      {/* Secciones temáticas */}
      {producto.descripcion && (
        <>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>DESCRIPCIÓN</Typography>
          <Typography variant="body1" sx={{ mb: 3 }}>{producto.descripcion}</Typography>
        </>
      )}

      {producto.tipo && (
        <>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>TIPO</Typography>
          <Typography variant="body2" sx={{ mb: 3 }}>{producto.tipo}</Typography>
        </>
      )}

      {producto.subtipo && (
        <>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>SUBTIPO</Typography>
          <Typography variant="body2" sx={{ mb: 3 }}>{producto.subtipo}</Typography>
        </>
      )}

      {producto.presentaciones && (
        <>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>PRESENTACIONES</Typography>
          <Typography variant="body2" sx={{ mb: 3 }}>{producto.presentaciones}</Typography>
        </>
      )}

      {producto.modoDeUso && (
        <>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>MODO DE USO</Typography>
          <Typography variant="body2" sx={{ mb: 4 }}>{producto.modoDeUso}</Typography>
        </>
      )}

      {/* Botones institucionales */}
      {producto.botones?.length > 0 && (
        <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2} sx={{ mb: 4 }}>
          {producto.botones.map((btn, index) => (
            <Button
              key={index}
              variant="contained"
              href={btn.url}
              target="_blank"
              sx={{
                bgcolor: altoContraste ? '#ffd900' : '#2e7d32',
                color: altoContraste ? '#000' : '#fff',
                fontWeight: 'bold',
                px: 3,
                '&:hover': {
                  bgcolor: altoContraste ? '#ffeb3b' : '#1b5e20'
                }
              }}
            >
              {btn.label}
            </Button>
          ))}
        </Stack>
      )}
    </Box>
  );
}
