import React from 'react';
import {
  Modal,
  Box,
  IconButton,
  Button,
  Typography,
  TextField,
  InputAdornment,
  Snackbar,
  Alert
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { Visibility, VisibilityOff } from '@mui/icons-material';

export default function LoginModal({ open, onClose }) {
  const [mostrarClave, setMostrarClave] = React.useState(false);
  const [errorLogin, setErrorLogin] = React.useState('');
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [emailActual, setEmailActual] = React.useState(localStorage.getItem('token-email'));
  const [camposVacios, setCamposVacios] = React.useState({ email: false, password: false });

  React.useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem('token');
      const email = localStorage.getItem('token-email');
      setIsLoggedIn(!!token);
      setEmailActual(email);
    };

    window.addEventListener('storage', checkToken);
    checkToken();
    return () => window.removeEventListener('storage', checkToken);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('token-email');
    setIsLoggedIn(false);
    setEmailActual(null);
    if (onClose) onClose();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const email = e.target.email?.value.trim();
    const password = e.target.password?.value.trim();

    const nuevosErrores = {
      email: !email,
      password: !password
    };
    setCamposVacios(nuevosErrores);
    if (nuevosErrores.email || nuevosErrores.password) return;

    if (email === 'admin@admin.com' && password === 'admin123') {
      localStorage.setItem('token', 'admin-token');
      localStorage.setItem('token-email', email);
      setIsLoggedIn(true);
      setEmailActual(email);
      window.dispatchEvent(new Event('storage'));
      if (onClose) onClose();
    } else if (email === 'demo@usuario.com' && password === '1234') {
      localStorage.setItem('token', 'usuario-token');
      localStorage.setItem('token-email', email);
      setIsLoggedIn(true);
      setEmailActual(email);
      window.dispatchEvent(new Event('storage'));
      if (onClose) onClose();
    } else {
      setErrorLogin('Usuario o contraseÃ±a incorrectos');
    }
  };

  return (
    <Modal
      open={open}
      onClose={onClose}
      aria-labelledby="login-modal"
      aria-describedby="modal de acceso"
      BackdropProps={{
        sx: {
          backgroundColor: '#fff'
        }
      }}
    >
      <Box
        component="form"
        role="dialog"
        aria-modal="true"
        onSubmit={handleSubmit}
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          bgcolor: 'white',
          p: 4,
          borderRadius: 3,
          width: { xs: '90%', sm: 420 },
          boxShadow: '0 10px 25px rgba(46,125,50,0.3)',
          outline: 0,
          display: 'flex',
          flexDirection: 'column',
          gap: 2
        }}
      >
        <IconButton
          onClick={onClose}
          sx={{
            position: 'absolute',
            top: 12,
            right: 12,
            color: '#388e3c'
          }}
        >
          <CloseIcon />
        </IconButton>

        {!isLoggedIn ? (
          <>
            <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#2e7d32', textAlign: 'center' }}>
              ð¿ Iniciar sesiÃ³n
            </Typography>

            <TextField
              name="email"
              label="Correo electrÃ³nico"
              type="email"
              fullWidth
              autoFocus
              variant="outlined"
              error={camposVacios.email}
              helperText={camposVacios.email ? 'Este campo es obligatorio' : ''}
              sx={{
                '& label.Mui-focused': { color: '#2e7d32' },
                '& .MuiOutlinedInput-root': {
                  '&.Mui-focused fieldset': {
                    borderColor: '#2e7d32',
                    boxShadow: '0 0 8px #4caf50',
                  },
                },
              }}
            />

            <TextField
              name="password"
              label="ContraseÃ±a"
              type={mostrarClave ? 'text' : 'password'}
              fullWidth
              variant="outlined"
              error={camposVacios.password}
              helperText={camposVacios.password ? 'Este campo es obligatorio' : ''}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      onClick={() => setMostrarClave(!mostrarClave)}
                      edge="end"
                      sx={{ color: '#2e7d32' }}
                    >
                      {mostrarClave ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                )
              }}
              sx={{
                '& label.Mui-focused': { color: '#2e7d32' },
                '& .MuiOutlinedInput-root': {
                  '&.Mui-focused fieldset': {
                    borderColor: '#2e7d32',
                    boxShadow: '0 0 8px #4caf50',
                  },
                },
              }}
            />

            <Button
              type="submit"
              variant="contained"
              fullWidth
              sx={{
                background: 'linear-gradient(45deg, #4caf50 30%, #2e7d32 90%)',
                color: 'white',
                fontWeight: 'bold',
                fontSize: '1rem',
                py: 1.2,
                borderRadius: 2,
                boxShadow: '0 6px 10px rgba(46,125,50,0.6)',
                transition: 'all 0.3s ease',
                '&:hover': {
                  background: 'linear-gradient(45deg, #66bb6a 30%, #388e3c 90%)',
                  boxShadow: '0 8px 15px rgba(46,125,50,0.8)',
                }
              }}
            >
              Entrar
            </Button>
          </>
        ) : (
          <Button
            variant="outlined"
            color="error"
            fullWidth
            onClick={handleLogout}
          >
            Cerrar sesiÃ³n
          </Button>
        )}

        <Snackbar
          open={!!errorLogin}
          autoHideDuration={4000}
          onClose={() => setErrorLogin('')}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert severity="error" variant="filled" onClose={() => setErrorLogin('')}>
            {errorLogin}
          </Alert>
        </Snackbar>
      </Box>
    </Modal>
  );
}
