import React from 'react';
import {
  Modal,
  Box,
  IconButton,
  Button,
  Typography,
  Link
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import LoginForm from '../forms/LoginForm';

export default function LoginModal({ onClose }) {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [emailActual, setEmailActual] = React.useState(localStorage.getItem('token-email'));

  React.useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem('token');
      const email = localStorage.getItem('token-email');
      setIsLoggedIn(!!token);
      setEmailActual(email);
    };

    window.addEventListener('storage', checkToken);
    checkToken(); // evaluar al montar

    return () => window.removeEventListener('storage', checkToken);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('token-email');
    setIsLoggedIn(false);
    setEmailActual(null);
    if (onClose) onClose();
    window.location.reload();
  };

  return (
    <Modal
      open
      onClose={onClose}
      aria-labelledby="modal-login"
      aria-describedby="login-accessible"
    >
      <Box
        role="dialog"
        aria-modal="true"
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          bgcolor: 'white',
          p: 4,
          borderRadius: 3,
          width: { xs: '90%', sm: 420 },
          boxShadow: 24,
          outline: 0,
          display: 'flex',
          flexDirection: 'column',
          gap: 2
        }}
      >
        {/* Botón cerrar modal */}
        <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
          <IconButton onClick={onClose} aria-label="Cerrar login">
            <CloseIcon />
          </IconButton>
        </Box>

        {/* Formulario si no está logueado */}
        {!isLoggedIn && (
          <>
            <LoginForm onClose={onClose} onLoginSuccess={() => {
              setIsLoggedIn(true);
              setEmailActual(localStorage.getItem('token-email'));
            }} />
          </>
        )}

        {/* Mensaje + botón si ya está logueado */}
        {isLoggedIn && emailActual && (
          <>
            <Typography variant="body1" align="center" sx={{ fontWeight: 'medium' }}>
              Sesión iniciada como <strong>{emailActual}</strong>
            </Typography>
            <Button
              variant="outlined"
              color="error"
              fullWidth
              onClick={handleLogout}
            >
              Cerrar sesión
            </Button>
          </>
        )}
      </Box>
    </Modal>
  );
}
