import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  Link,
  TextField,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import productos from '../../data/productos.json';
import LoginModal from '../../components/modals/LoginModal'; // ⬅️ Nuevo modal flotante

export default function Comprar() {
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('token'));
  const categorias = ['Alimento Perro', 'Alimento Gato', 'Limpieza Y Aseo', 'Jardinería'];
  const [filtro, setFiltro] = useState('Alimento Perro');
  const [showLogin, setShowLogin] = useState(false); // ⬅️ Estado para el modal

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      setShowLogin(true); // ⬅️ Abrir modal en lugar de redireccionar
    } else {
      navigate(`/comprar/${id}`);
    }
  };

  const destacados = productos.filter(p => p.categoria === filtro).slice(0, 4);
  const masVendidos = productos.filter(p => p.ventas >= 50).slice(0, 4);
  const ultimos = productos.slice(-4);

  const renderProductos = (lista) => (
    <Grid container spacing={4}>
      {lista.map(prod => (
        <Grid item xs={12} sm={6} md={3} key={prod.id}>
          <Paper elevation={0} sx={{ p: 2, borderRadius: 2, position: 'relative' }}>
            {prod.descuento && (
              <Box
                sx={{
                  position: 'absolute',
                  top: 12,
                  right: 12,
                  bgcolor: '#f44336',
                  color: '#fff',
                  px: 1.5,
                  py: 0.5,
                  borderRadius: 1,
                  fontSize: '0.75rem'
                }}
              >
                -{prod.descuento}%
              </Box>
            )}
            <Box
              component="img"
              src={prod.imagen}
              alt={prod.nombre}
              sx={{
                width: '100%',
                borderRadius: 2,
                objectFit: 'cover'
              }}
            />
            <Box sx={{ mt: 1, display: 'flex', alignItems: 'baseline', gap: 1 }}>
              <Typography sx={{ color: '#d32f2f', fontWeight: 'bold', fontSize: '1.1rem' }}>
                {typeof prod.precio === 'number' ? `$${prod.precio.toFixed(2)}` : 'Precio no disponible'}
              </Typography>
              {typeof prod.precioOriginal === 'number' && (
                <Typography sx={{ color: '#999', textDecoration: 'line-through', fontSize: '0.85rem' }}>
                  ${prod.precioOriginal.toFixed(2)}
                </Typography>
              )}
            </Box>
            <Typography variant="caption" sx={{ color: '#666', display: 'block', mb: 1 }}>
              (Exento de IVA)
            </Typography>
            <Link
              href={`/productos/detalle/${prod.id}`}
              sx={{
                display: 'block',
                fontWeight: 'bold',
                color: '#388e3c',
                textDecoration: 'underline',
                mb: 1,
                fontSize: '0.875rem'
              }}
            >
              {prod.nombre}
            </Link>
            <Button
              fullWidth
              onClick={() => handleCompra(prod.id)}
              sx={{
                border: '1px solid #fb8c00',
                color: '#fb8c00',
                textTransform: 'none',
                borderRadius: 2,
                py: 1
              }}
            >
              Agregar
            </Button>
          </Paper>
        </Grid>
      ))}
    </Grid>
  );

  return (
    <Box sx={{ pt: 12, pb: 6, px: { xs: 2, md: 4 } }}>
      {/* Encabezado institucional */}
      <Box
        sx={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bgcolor: 'white',
          borderBottom: '1px solid #ddd',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          px: 3,
          py: 1,
          zIndex: 1000,
        }}
      >
        <Typography
          variant="h6"
          component="a"
          href="/"
          sx={{
            color: '#2e7d32',
            fontWeight: 'bold',
            textDecoration: 'none',
            fontSize: '1.3rem',
            cursor: 'pointer',
            '&:hover': { textDecoration: 'underline' }
          }}
        >
          Mercado Agrícola
        </Typography>

        <TextField
          size="small"
          placeholder="Buscar producto"
          sx={{
            bgcolor: '#f1f8e9',
            borderRadius: 20,
            width: { xs: 140, md: 240 },
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon sx={{ color: 'gray' }} />
              </InputAdornment>
            )
          }}
        />

        {/* Abrir modal en lugar de redirigir */}
        <Button
          variant="outlined"
          onClick={() => setShowLogin(true)}
          sx={{
            color: '#2e7d32',
            borderColor: '#2e7d32',
            fontWeight: 'bold',
            textTransform: 'uppercase',
            fontSize: '0.75rem',
          }}
        >
          Iniciar sesión
        </Button>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, cursor: 'pointer' }}>
          <Typography sx={{ fontSize: '1.2rem' }}>🛒</Typography>
          <Typography sx={{ fontWeight: 'bold', fontSize: '0.9rem' }}>$0.00</Typography>
        </Box>
      </Box>

      {/* Filtros por categoría */}
      <Box sx={{ mt: 10, mb: 4, display: 'flex', gap: 2, flexWrap: 'wrap' }}>
        {categorias.map(cat => (
          <Button
            key={cat}
            onClick={() => setFiltro(cat)}
            variant={filtro === cat ? 'contained' : 'outlined'}
            sx={{
              textTransform: 'none',
              borderRadius: 20,
              borderColor: '#2e7d32',
              color: filtro === cat ? '#fff' : '#2e7d32',
              bgcolor: filtro === cat ? '#2e7d32' : 'transparent',
              px: 3,
            }}
          >
            {cat}
          </Button>
        ))}
      </Box>

      {/* Secciones de productos */}
      <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 3 }}>
        PRODUCTOS <Box component="span" sx={{ color: '#2e7d32' }}>DESTACADOS</Box>
      </Typography>
      {renderProductos(destacados)}

      <Typography variant="h6" sx={{ fontWeight: 'bold', mt: 6, mb: 2, color: '#2e7d32' }}>
        MÁS VENDIDOS
      </Typography>
      {renderProductos(masVendidos)}

      <Typography variant="h6" sx={{ fontWeight: 'bold', mt: 6, mb: 2, color: '#2e7d32' }}>
        ÚLTIMOS VENDIDOS
      </Typography>
      {renderProductos(ultimos)}

      {/* Mostrar modal flotante si lo activás */}
      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
    </Box>
  );
}
