import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  IconButton,
  InputAdornment,
  Snackbar,
  Alert
} from '@mui/material';
import { Visibility, VisibilityOff, Close } from '@mui/icons-material';

export default function LoginForm({ onClose, onLoginSuccess }) {
  const [mostrarClave, setMostrarClave] = React.useState(false);
  const [errorLogin, setErrorLogin] = React.useState('');
  const [camposVacios, setCamposVacios] = React.useState({ email: false, password: false });

  const onSubmit = (data) => {
    const email = data.email;
    const password = data.password;

    const nuevosErrores = {
      email: !email,
      password: !password
    };
    setCamposVacios(nuevosErrores);
    if (nuevosErrores.email || nuevosErrores.password) return;

    if (email === 'admin@admin.com' && password === 'admin123') {
      localStorage.setItem('token', 'admin-token');
      localStorage.setItem('token-email', email);
      localStorage.setItem('rol', 'admin');
      if (onLoginSuccess) onLoginSuccess();
      if (onClose) onClose();
      window.dispatchEvent(new Event('storage'));
    } else if (email === 'demo@usuario.com' && password === '1234') {
      localStorage.setItem('token', 'usuario-token');
      localStorage.setItem('token-email', email);
      localStorage.setItem('rol', 'usuario');
      if (onLoginSuccess) onLoginSuccess();
      if (onClose) onClose();
      window.dispatchEvent(new Event('storage'));
    } else {
      setErrorLogin('Usuario o contraseña incorrectos');
    }
  };

  return (
    <Box
      component="form"
      onSubmit={(e) => {
        e.preventDefault();
        onSubmit({
          email: e.target.email?.value.trim(),
          password: e.target.password?.value.trim()
        });
      }}
      noValidate
      sx={{
        position: 'relative',
        maxWidth: 360,
        mx: 'auto',
        mt: 12,
        p: 4,
        borderRadius: 3,
        boxShadow: '0 10px 25px rgba(46, 125, 50, 0.3)',
        display: 'flex',
        flexDirection: 'column',
        gap: 2.5,
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        backgroundColor: 'white',
      }}
    >
      {/* Botón cerrar dentro del cuadro */}
      <IconButton
        onClick={onClose}
        aria-label="Cerrar"
        size="small"
        sx={{
          position: 'absolute',
          top: 8,
          right: 8,
          color: '#2e7d32',
        }}
      >
        <Close fontSize="small" />
      </IconButton>

      <Typography
        variant="h5"
        sx={{
          fontWeight: 'bold',
          color: '#2e7d32',
          textAlign: 'center',
          mb: 1
        }}
      >
        🌿 Iniciar sesión
      </Typography>

      <TextField
        name="email"
        label="Correo electrónico"
        type="email"
        fullWidth
        autoFocus
        autoComplete="username"
        variant="outlined"
        error={camposVacios.email}
        helperText={camposVacios.email ? 'Este campo es obligatorio' : ''}
        sx={{
          '& label.Mui-focused': { color: '#2e7d32' },
          '& .MuiOutlinedInput-root': {
            '&.Mui-focused fieldset': {
              borderColor: '#2e7d32',
              boxShadow: '0 0 8px #4caf50',
            },
          },
        }}
      />

      <TextField
        name="password"
        label="Contraseña"
        type={mostrarClave ? 'text' : 'password'}
        fullWidth
        autoComplete="current-password"
        variant="outlined"
        error={camposVacios.password}
        helperText={camposVacios.password ? 'Este campo es obligatorio' : ''}
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton
                onClick={() => setMostrarClave(!mostrarClave)}
                aria-label={mostrarClave ? 'Ocultar contraseña' : 'Mostrar contraseña'}
                edge="end"
                sx={{ color: '#2e7d32' }}
              >
                {mostrarClave ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          )
        }}
        sx={{
          '& label.Mui-focused': { color: '#2e7d32' },
          '& .MuiOutlinedInput-root': {
            '&.Mui-focused fieldset': {
              borderColor: '#2e7d32',
              boxShadow: '0 0 8px #4caf50',
            },
          },
        }}
      />

      <Button
        type="submit"
        variant="contained"
        fullWidth
        sx={{
          background: 'linear-gradient(45deg, #4caf50 30%, #2e7d32 90%)',
          color: 'white',
          fontWeight: 'bold',
          fontSize: '1rem',
          paddingY: 1.3,
          borderRadius: 3,
          boxShadow: '0 6px 10px rgba(46,125,50,0.6)',
          transition: 'all 0.3s ease',
          '&:hover': {
            background: 'linear-gradient(45deg, #66bb6a 30%, #388e3c 90%)',
            boxShadow: '0 8px 15px rgba(46,125,50,0.8)',
          }
        }}
      >
        Entrar
      </Button>

      <Typography
        variant="body2"
        align="center"
        sx={{ mt: 1, color: '#2e7d32', fontWeight: '600' }}
      >
        ¿No tienes cuenta?{' '}
        <a href="/registro" style={{ fontWeight: 'bold', color: '#388e3c', textDecoration: 'underline' }}>
          Regístrate aquí
        </a>
      </Typography>

      <Snackbar
        open={!!errorLogin}
        autoHideDuration={4000}
        onClose={() => setErrorLogin('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert severity="error" variant="filled" onClose={() => setErrorLogin('')}>
          {errorLogin}
        </Alert>
      </Snackbar>
    </Box>
  );
}
